package conditional;

public class ElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=20;
if(a==30) {
	System.out.println("a value is 30");
}
else if(a==20) {
	System.out.println("a value is 20");
}
else if(a==25) {
	System.out.println("a value is 25");
}
else { 
	System.out.println("else statement");
}
	}

}
