package operators;

public class Assignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10;
int b=20;
System.out.println("+= operator" +(a+=b));
System.out.println("-= operator" +(a-=b));
System.out.println("*= operator" +(a*=b));
System.out.println("/= operator" +(a/=b));
System.out.println("%= operator" +(a%=b));
	}

}
