package operators;

public class Relational {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int x,y;
x=10;
y=20;
System.out.println("equal to" +(x==y));
System.out.println("not equal to" +(x!=y));
System.out.println("greaterthan" +(x>y));
System.out.println("lessthan" +(x<y));
System.out.println("greaterthan or equal" +(x>=y));
System.out.println("lessthan or equal" +(x<=y));
	}

}
